import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Styles from "./LaunchTable.module.css";

const formatDate = (date) =>
  new Intl.DateTimeFormat("en", {
    day: "numeric",
    month: "long",
    year: "numeric",
    weekday: "long",
  }).format(new Date(date));

const LaunchTable = () => {
  const [launches, setLaunches] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [launchesPerPage] = useState(10);
  const [maxPage, setMaxPage] = useState(1);
  const [sortKey, setSortKey] = useState("date_utc");
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    axios
      .get("https://api.spacexdata.com/v4/launches")
      .then((response) => {
        console.log(response.data[0]);
        const data = response.data;
        setMaxPage(Math.ceil(response.data.length / launchesPerPage));
        return data;
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      })
      .finally((data) => {
        console.log("1", data);
      });
  }, [launchesPerPage]);

  useEffect(() => {
    const updatedLaunches = launches.map((launch) => {
      const favoriteStatus = localStorage.getItem(`favorite_${launch.id}`);
      if (favoriteStatus) {
        launch.isFavorite = JSON.parse(favoriteStatus);
      } else {
        launch.isFavorite = false;
      }
      return launch;
    });
    setLaunches(updatedLaunches);
  }, []);

  const toggleFavorite = (launchId) => {
    const updatedLaunches = launches.map((launch) => {
      if (launch.id === launchId) {
        const newFavoriteStatus = !launch.isFavorite;
        launch.isFavorite = newFavoriteStatus;
        localStorage.setItem(
          `favorite_${launch.id}`,
          JSON.stringify(newFavoriteStatus)
        );
      }
      return launch;
    });
    setLaunches(updatedLaunches);
  };

  function handleNextPage(e) {
    e.preventDefault();
    if (currentPage === maxPage) return;
    setCurrentPage(() => currentPage + 1);
  }

  function handlePreviousPage(e) {
    e.preventDefault();
    if (currentPage <= 1) return;
    setCurrentPage(() => currentPage - 1);
  }

  const indexOfLastLaunch = currentPage * launchesPerPage;
  const indexOfFirstLaunch = indexOfLastLaunch - launchesPerPage;
  const currentLaunches = launches.slice(indexOfFirstLaunch, indexOfLastLaunch);

  const sortedLaunches = currentLaunches.sort((a, b) => {
    const aValue = a[sortKey];
    const bValue = b[sortKey];

    if (aValue < bValue) {
      return sortOrder === "asc" ? -1 : 1;
    }
    if (aValue > bValue) {
      return sortOrder === "asc" ? 1 : -1;
    }
    return 0;
  });

  const handleSort = (key) => {
    if (key === sortKey) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortKey(key);
      setSortOrder("asc");
    }
  };

  const renderLaunches = sortedLaunches.map((launch) => (
    <tr key={launch.id}>
      <td>{launch.name}</td>
      <td>{formatDate(launch.date_utc || null)}</td>
      <td>
        {launch.links.patch.small ? (
          <img
            className={Styles.Image}
            src={launch.links.patch.small}
            alt={launch.name}
          />
        ) : (
          "N/A"
        )}
      </td>
      <td>
        <Link className={Styles.Link} to={`/launch/${launch.id}`}>
          Details
        </Link>
      </td>
    </tr>
  ));

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort("name")}>Name</th>
            <th onClick={() => handleSort("date_utc")}>Date</th>
            <th>Rocket Image</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody>{renderLaunches}</tbody>
      </table>

      <div className={Styles.ButtonWrap}>
        <button className={Styles.Button} onClick={handlePreviousPage}>
          Previous
        </button>
        <div>{`Page ${currentPage}`}</div>
        <button className={Styles.Button} onClick={handleNextPage}>
          Next
        </button>
      </div>
    </div>
  );
};

export default LaunchTable;
