import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import Styles from "./LaunchDetails.module.css";

const LaunchDetails = () => {
  const [launch, setLaunch] = useState({});
  const [isFavorite, setIsFavorite] = useState(false);

  const location = useLocation();
  const { launchData } = location.state;
  console.log(launchData);

  useEffect(() => {
    setLaunch(launchData);

    const favoriteStatus = localStorage.getItem(`favorite_${launchData.id}`);
    if (favoriteStatus) {
      setIsFavorite(JSON.parse(favoriteStatus));
    }
  }, [launchData]);

  const toggleFavorite = () => {
    const newFavoriteStatus = !isFavorite;
    setIsFavorite(newFavoriteStatus);

    // Store the updated favorite status in localStorage
    localStorage.setItem(
      `favorite_${launchData.id}`,
      JSON.stringify(newFavoriteStatus)
    );
  };

  return (
    <div>
      {launch.links?.patch.small ? (
        <img
          className={Styles.Image}
          src={launch.links.patch.small}
          alt={launch.name}
        />
      ) : (
        "N/A"
      )}
      <div className={Styles.Wrapper}>
        <h2>{launch.name}</h2>
        <p>Date: {launch.date_utc}</p>
      </div>
      <button onClick={toggleFavorite}>Toggle Favorite</button>
      <Link className={Styles.Back} to={"/"}>
        Back
      </Link>
    </div>
  );
};

export default LaunchDetails;
