import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const LaunchTable = () => {
  const [launches, setLaunches] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [launchesPerPage] = useState(10);

  useEffect(() => {
    axios
      .get("https://api.spacexdata.com/v4/launches")
      .then((response) => {
        setLaunches(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  function handleNextPage(e) {
    e.preventDefault();
    setCurrentPage(() => currentPage + 1);
  }

  function handlePreviousPage(e) {
    e.preventDefault();
    setCurrentPage(() => setCurrecurrentPagentPage - 1);
  }

  const indexOfLastLaunch = currentPage * launchesPerPage;
  const indexOfFirstLaunch = indexOfLastLaunch - launchesPerPage;
  const currentLaunches = launches.slice(indexOfFirstLaunch, indexOfLastLaunch);

  const renderLaunches = currentLaunches.map((launch) => (
    <tr key={launch.id}>
      <td>{launch.name}</td>
      <td>{launch.date_utc}</td>
      <td>{launch.rocket.name}</td>
      <td>
        {launch.links.mission_patch ? (
          <img src={launch.links.mission_patch} alt={launch.name} />
        ) : (
          "N/A"
        )}
      </td>
      <td>
        <Link to={`/launch/${launch.id}`}>Details</Link>
      </td>
    </tr>
  ));

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Date</th>
            <th>Rocket</th>
            <th>Mission Patch</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody>{renderLaunches}</tbody>
      </table>

      <div>
        <button onClick={handlePreviousPage}> Previous</button>
        <button onClick={handleNextPage}>Next</button>
      </div>
    </div>
  );
};

export default LaunchTable;
