import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Syles from "./LaunchTable.module.css";

const LaunchTable = () => {
  const [launches, setLaunches] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [launchesPerPage] = useState(10);
  const [maxPage, setMaxPage] = useState(1);
  const [sortKey, setSortKey] = useState("name");
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    axios
      .get("https://api.spacexdata.com/v4/launches")
      .then((response) => {
        setLaunches(response.data);
        setMaxPage(Math.ceil(response.data.length / launchesPerPage));
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  function handleNextPage(e) {
    e.preventDefault();
    if (currentPage === maxPage) return;
    setCurrentPage(() => currentPage + 1);
  }

  function handlePreviousPage(e) {
    e.preventDefault();
    if (currentPage <= 1) return;
    setCurrentPage(() => currentPage - 1);
  }

  const indexOfLastLaunch = currentPage * launchesPerPage;
  const indexOfFirstLaunch = indexOfLastLaunch - launchesPerPage;
  const currentLaunches = launches.slice(indexOfFirstLaunch, indexOfLastLaunch);

  const sortedLaunches = currentLaunches.sort((a, b) => {
    const aValue = a[sortKey];
    const bValue = b[sortKey];

    if (aValue < bValue) {
      return sortOrder === "asc" ? -1 : 1;
    }
    if (aValue > bValue) {
      return sortOrder === "asc" ? 1 : -1;
    }
    return 0;
  });

  const handleSort = (key) => {
    if (key === sortKey) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortKey(key);
      setSortOrder("asc");
    }
  };

  const renderLaunches = sortedLaunches.map((launch) => (
    <tr key={launch.id}>
      <td>{launch.name}</td>
      <td>{launch.date_utc}</td>
      <td>{launch.rocket.name}</td>
      <td>
        {launch.links.mission_patch ? (
          <img src={launch.links.mission_patch} alt={launch.name} />
        ) : (
          "N/A"
        )}
      </td>
      <td>
        <Link to={`/launch/${launch.id}`}>Details</Link>
      </td>
    </tr>
  ));

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort("name")}>Name</th>
            <th onClick={() => handleSort("date_utc")}>Date</th>
            <th onClick={() => handleSort("rocket.name")}>Rocket</th>
            <th>Mission Patch</th>
            <th>Details</th>
          </tr>
        </thead>
        <tbody>{renderLaunches}</tbody>
      </table>

      <div className={Syles.ButtonWrap}>
        <button className={Syles.Button} onClick={handlePreviousPage}>
          Previous
        </button>
        <div>{`Page ${currentPage}`}</div>
        <button className={Syles.Button} onClick={handleNextPage}>
          Next
        </button>
      </div>
    </div>
  );
};

export default LaunchTable;
