import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const LaunchDetails = () => {
  const { id } = useParams();
  const [launch, setLaunch] = useState({});

  useEffect(() => {
    // Fetch data for the selected launch
    axios
      .get(`https://api.spacexdata.com/v4/launches/${id}`)
      .then((response) => {
        setLaunch(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [id]);

  const toggleFavorite = () => {
    // Implement logic to toggle the favorite status
  };

  return (
    <div>
      <h2>{launch.name}</h2>
      <p>Date: {launch.date_utc}</p>
      <p>Rocket: {launch.rocket?.name}</p>
      <p>
        Mission Patch:{" "}
        {launch.links?.mission_patch ? (
          <img src={launch.links.mission_patch} alt={launch.name} />
        ) : (
          "N/A"
        )}
      </p>
      <button onClick={toggleFavorite}>Toggle Favorite</button>
    </div>
  );
};

export default LaunchDetails;
