import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const LaunchDetails = () => {
  const { id } = useParams();
  const [launch, setLaunch] = useState({});
  const [isFavorite, setIsFavorite] = useState(false);

  useEffect(() => {
    const favoriteStatus = localStorage.getItem(`favorite_${id}`);
    if (favoriteStatus) {
      setIsFavorite(JSON.parse(favoriteStatus));
    }

    axios
      .get(`https://api.spacexdata.com/v4/launches/${id}`)
      .then((response) => {
        setLaunch(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [id]);

  const toggleFavorite = () => {
    const newFavoriteStatus = !isFavorite;
    setIsFavorite(newFavoriteStatus);

    // Store the updated favorite status in localStorage
    localStorage.setItem(`favorite_${id}`, JSON.stringify(newFavoriteStatus));
  };

  return (
    <div>
      <h2>{launch.name}</h2>
      <p>Date: {launch.date_utc}</p>
      <p>Rocket: {launch.rocket?.name}</p>
      <p>
        Mission Patch:{" "}
        {launch.links?.mission_patch ? (
          <img src={launch.links.mission_patch} alt={launch.name} />
        ) : (
          "N/A"
        )}
      </p>
      <button onClick={toggleFavorite}>Toggle Favorite</button>
      <Link className={Styles.Link} to={`/launch/${launch.id}`}>
        Launch table
      </Link>
    </div>
  );
};

export default LaunchDetails;
